import React from "react"
import Card from "../Card"
import "./style.css"

class Counter extends React.Component {
    state = {
      count: 0
    };

    handleIncrement = () => {
      this.setState({ count: this.state.count + 1 });
    };
    handleDecrement = () => {
      this.setState({count: this.state.count - 1});
    }


    render() {
      return (
        <div className="jumbotron">
