import React from "react"
import Wrapper from "../Wrapper"
import Header from "../Header"
import cards from "../../cards.json"
import Card from "../Card"


class CardContainer extends React.Component{
    state = {
    images: cards,
    score:0,
    topScore: 0,
    message: ""
  }

  gameOver = () => {
    if (this.state.score > this.state.topScore) {
      this.setState({highscore: this.state.score}, function() {
        console.log(this.state.highscore);
      });
    }
    this.state.images.forEach(card => {
      card.count = 0;
    });
    alert(`Game Over :( \nscore: ${this.state.score}`);
    this.setState({score: 0});
    return true;
  }

  handleClick = (id, clicked) => {
		const imageOrder = this.state.images;
    console.log(this.state.message)

		if (clicked) {
			imageOrder.forEach((image, index) => {
				imageOrder[index].clicked = false;
			});
			return this.setState({
				image: imageOrder.sort(() => Math.random() - 0.5),
				message: "Sorry, try again!",
        score: 0
      })
    }

		else {
			imageOrder.forEach((image, index) => {
				if (id === image.id) {
					imageOrder[index].clicked = true;
				}
			});

			const { topScore, score } = this.state;
			const newScore = score + 1;
			const newTopScore = newScore > topScore ? newScore : topScore;

			return this.setState({
				image: imageOrder.sort(() => Math.random() - 0.5),
				message: "Awesome, you got it!",
				score: newScore,
				topScore: newTopScore,
      })
    }
	};


      render(){
        return(
          <div>
            <Header score={this.state.score} topScore={this.state.topScore} message = {this.state.message}/>
            <Wrapper>
            {this.state.images.map(single=> (
              <Card
              // shuffle={this.shuffle}
              clicked = {false}
              clickHandle={this.handleClick}
              id={single.id}
              shuffle={this.shuffle}
              key={single.id}
              name={single.name}
              image={single.image}
              />
            ))}
          </Wrapper>
          </div>

        )
    }
  }

  export default CardContainer
