import React from "react"
import Wrapper from "../Wrapper"
import Header from "../Header"
import cards from "../../cards.json"
import Card from "../Card"


class CardContainer extends React.Component{
    state = {
    images: cards,
    clickedArray: [],
    score:0,
    topScore: 0,
    message: ""
  }

  gameOver = () => {
    if (this.state.score > this.state.topScore) {
      this.setState({highscore: this.state.score}, function() {
        console.log(this.state.highscore);
      });
    }
    this.state.images.forEach(card => {
      card.count = 0;
    });
    alert(`Game Over :( \nscore: ${this.state.score}`);
    this.setState({score: 0});
    return true;
  }

  handleClick = (id) => {
     const shuffledArray = this.shuffleArray(images);
     this.setState({images: shuffledArray});
     // if clicked an image already clicked set this.state.score = 0; empty clickeadArray, end of if block
     if (this.state.clickedArray.includes(id)) {
       this.setState({ score: 0, clickedArray: [], message: "Incorrect!! Game Over â˜¹ï¸ Click an image to start again!", shakeit: "true"});
     }
     else {
       this.setState({
         clickedArray: this.state.clickedArray.concat([id]),
         score: this.state.score + 1,
         message: "Correct!! ðŸ™‚",
         shakeit: "false"
       });
     }
     // set topscore = score if score>topscore.
     if (this.state.score > this.state.topScore) {
       this.setState({ topScore: this.state.score });
     }
  };

  shuffleArray = (picturesArray) => {
    for (let i = picturesArray.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [picturesArray[i], picturesArray[j]] = [picturesArray[j], picturesArray[i]];
    }
    return picturesArray;
}


      render(){
        return(
          <div>
            <Header score={this.state.score} topScore={this.state.topScore} message = {this.state.message}/>
            <Wrapper>
            {this.state.images.map(single=> (
              <Card
              // shuffle={this.shuffle}
              clicked = {false}
              clickHandle={this.handleClick}
              id={single.id}
              shuffle={this.shuffle}
              key={single.id}
              name={single.name}
              image={single.image}
              />
            ))}
          </Wrapper>
          </div>

        )
    }
  }

  export default CardContainer
